hotspot.onclick:
lookto(get(ath), 5, 35);
      loadscene(parlour, null, MERGE,BLEND(1));
      lookat(145, 0, 130);
      set(view.architectural, 1.0);
      wait(BLEND);
      tween(view.architectural, 0.0, 2.0);
      oninterrupt( tween(view.architectural,0.0, 0.5) );
      lookto(180, 0, 130, smooth(100, 100, 200));

lookto(get(ath), 5, 35);
      loadscene(bedroom2, null, MERGE,BLEND(1));
      lookat(145, 0, 130);
      set(view.architectural, 1.0);
      wait(BLEND);
      tween(view.architectural, 0.0, 2.0);
      oninterrupt( tween(view.architectural,0.0, 0.5) );
      lookto(0, 0, 130, smooth(100, 100, 200));

lookto(get(ath), 5, 35);
      loadscene(bedroom3, null, MERGE,BLEND(1));
      lookat(145, 0, 130);
      set(view.architectural, 1.0);
      wait(BLEND);
      tween(view.architectural, 0.0, 2.0);
      oninterrupt( tween(view.architectural,0.0, 0.5) );
      lookto(0, 0, 130, smooth(100, 100, 200));

lookto(get(ath), 5, 35);
